import React, { Component } from 'react';
import { MapContainer, CircleMarker, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";

class Map extends Component {
    constructor(props) {
		super(props);

		this.state = {
			countries: [],
		};

		// this.submitDecadeGenre = this.submitDecadeGenre.bind(this);
		// this.handleDecadeChange = this.handleDecadeChange.bind(this);
		// this.handleGenreChange = this.handleGenreChange.bind(this);
	};

	/* ---- Q3a (Best Movies) ---- */
	componentDidMount() {

		fetch("http://localhost:8081/test",
		{
		  method: 'GET' // The type of HTTP request.
		}).then(res => {
		  // Convert the response data to a JSON.
		  console.log(res.json());
          return res.json();
		}, err => {
		  // Print the error if there is one.
		  console.log(err);
	  }).then(casesList => {
        if (!casesList) return;
        const countriesDivs = casesList.map((casesObj, i) =>
        <CircleMarker
        center={[casesObj.latitude, casesObj.longitude]}
      />
      );

      // Set the state of the keywords list to the value returned by the HTTP response from the server.
      this.setState({
        countries:countriesDivs
      });
      }, err => {
        // Print the error if there is one.
        console.log(err);
      });

	};
  render() {
    return (
      <div>
        <MapContainer
          style={{ height: "480px", width: "100%" }}
          zoom={1}
          center={[-0.09, 51.505]}>
              <TileLayer url="http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
              <div className="movies-container" id="results">
			          {this.state.countries}
			        </div>
        </MapContainer>
      </div>
    );
  }
}

export default Map;
