import React, { Component } from 'react';
import { MapContainer, CircleMarker, TileLayer,Tooltip } from "react-leaflet";

var CanvasJSReact = require('./canvasjs.react');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
export default class Nutrition extends React.Component {
    constructor(props) {
		super(props);

		this.state = {
			countries: [],
            selectedCountry: "",
            chart: [],
            options: []
		};

		this.submitCountryChange = this.submitCountryChange.bind(this);
	    this.handleCountryChange = this.handleCountryChange.bind(this);
		// this.handleGenreChange = this.handleGenreChange.bind(this);
	};

	/* ---- Q3a (Best Movies) ---- */
	componentDidMount() {
        console.log("HELLO");
        //fetch the weeks options
        fetch("http://localhost:8081/nutrition",
		{
		  method: 'GET' // The type of HTTP request.
		}).then(res => {
		  // Convert the response data to a JSON.
		  return res.json();
		}, err => {
		  // Print the error if there is one.
		  console.log(err);
	  }).then(countryList => {
		  if (!countryList) return;

		  const countryDivs = countryList.map((countryObj, i) =>
		  <option
		      key={i}
		      className="countryOption"
			  value={countryObj.Country}>{countryObj.Country}
		  </option>
		  );
        //   console.log(decadesDivs);
		//    console.log(decadesList);
		  this.setState({
			countries: countryDivs,
			selectedCountry: countryDivs[0].props.value,
		  });
		}, err => {
		  // Print the error if there is one.
		  console.log(err);
		});

	};

    handleCountryChange(e) {
        let tmp = e.target.value;
		this.setState({
			selectedCountry: tmp
		});

	};

    submitCountryChange(e) {
        fetch("http://localhost:8081/byCountry/"+this.state.selectedCountry,
        {
            method: 'GET' // The type of HTTP request.
        }).then(res =>{
            return res.json();
        }, err => {
            // Print the error if there is one.
            console.log(err);
        }).then(nutr => {
            if (!nutr) return;
            const option = {
                exportEnabled: true,
                animationEnabled: true,
                title: {
                    text: "Website Traffic Sources"
                },
                data: [{
                    type: "pie",
                    startAngle: 75,
                    toolTipContent: "<b>{label}</b>: {y}%",
                    showInLegend: "true",
                    legendText: "{label}",
                    indexLabelFontSize: 16,
                    indexLabel: "{label} - {y}%",
                    dataPoints: [
                        { y: 18, label: "Direct" },
                        { y: 49, label: "Organic Search" },
                        { y: 9, label: "Paid Search" },
                        { y: 5, label: "Referral" },
                        { y: 19, label: "Social" }
                    ]
                }]
            };

        // Set the state of the keywords list to the value returned by the HTTP response from the server.
        this.setState({
            options:option
        });
    }, err => {
        // Print the error if there is one.
        console.log(err);
      });

	};
  render() {
    return (
    <div id = "nutrition">
            <h1>HELLO</h1>
        <div className="dropdown-container">
            <select value={this.state.selectedCountry} onChange={this.handleCountryChange} className="dropdown" id="countryDropdown">
                {this.state.countries}
            </select>
            <button className="submit-btn" id="submitBtn" onClick={this.submitCountryChange}>Submit</button>
        </div>
        <div className="chart" id="chart">
        <CanvasJSChart options = {this.state.options}/>;
		</div>
    </div>
    );
  }
}
