import React, { Component, Chart } from 'react';
import { MapContainer, CircleMarker, TileLayer,Tooltip } from "react-leaflet";
import { PieChart } from 'react-minimal-pie-chart';

export default class Nutrition extends React.Component {
    constructor(props) {
		super(props);

		this.state = {
			countries: [],
            selectedCountry: "",
            chart: "",
            options: ""
		};

		this.submitCountryChange = this.submitCountryChange.bind(this);
	    this.handleCountryChange = this.handleCountryChange.bind(this);
		// this.handleGenreChange = this.handleGenreChange.bind(this);
	};

	/* ---- Q3a (Best Movies) ---- */
	componentDidMount() {
        console.log("HELLO");
        //fetch the weeks options
        fetch("http://localhost:8081/nutrition",
		{
		  method: 'GET' // The type of HTTP request.
		}).then(res => {
		  // Convert the response data to a JSON.
		  return res.json();
		}, err => {
		  // Print the error if there is one.
		  console.log(err);
	  }).then(countryList => {
		  if (!countryList) return;

		  const countryDivs = countryList.map((countryObj, i) =>
		  <option
		      key={i}
		      className="countryOption"
			  value={countryObj.Country}>{countryObj.Country}
		  </option>
		  );
        //   console.log(decadesDivs);
		//    console.log(decadesList);
		  this.setState({
			countries: countryDivs,
			selectedCountry: countryDivs[0].props.value,
		  });
		}, err => {
		  // Print the error if there is one.
		  console.log(err);
		});

	};

    handleCountryChange(e) {
        let tmp = e.target.value;
		this.setState({
			selectedCountry: tmp
		});

	};

    submitCountryChange(e) {
        fetch("http://localhost:8081/byCountry/"+this.state.selectedCountry,
        {
            method: 'GET' // The type of HTTP request.
        }).then(res =>{
            return res.json();
        }, err => {
            // Print the error if there is one.
            console.log(err);
        }).then(nutr => {
            if (!nutr) return;
            const pieChart = nutr.map((nutrObj, i) =>

            <PieChart

            // labelPosition="25%"

            label={(x) => { return x.dataEntry.title;}}
            labelStyle={{ color: "white",fontSize:"2px" }}
            labelPosition="105"
            radius = "35"
            background='#FFF0F5'
            data={[
                { title: 'Alcoholic Beverages', value: nutrObj.Alcoholic_Beverages/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#E38627' },
                { title: 'Animal Fats', value: nutrObj.Animal_fats/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#C13C37' },
                { title: 'Animal Products', value: nutrObj.Animal_Products/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#6A2135' },
                { title: 'Cereals', value: nutrObj.Cereals/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#FFE4C4' },
                { title: 'Fish and Seafood', value: nutrObj.Fish_Seafood/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#black' },
                { title: 'Fruits', value: nutrObj.Fruits/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#9932CC' },
                { title: 'Milk', value: nutrObj.Milk/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#E9967A' },
                { title: 'Starchy roots', value: nutrObj.Starchy_Roots/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#F0FFF0' },
                { title: 'Sugar', value: nutrObj.Sugar/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#F0E68C' },
                { title: 'Vegetable Oil', value: nutrObj.Vegetable_Oils/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#FFF0F5' },
                { title: 'Vegetable Products', value: nutrObj.Vegetable_Products/(nutrObj.Alcoholic_Beverages+nutrObj.Animal_fats+ nutrObj.Animal_Products+nutrObj.Cereals+nutrObj.Fish_Seafood+nutrObj.Fruits+nutrObj.Milk+nutrObj.Starchy_Roots+nutrObj.Sugar+nutrObj.Vegetable_Oils+nutrObj.Vegetable_Products)*100, color: '#FFF0F5' },
            ]}
            viewBoxSize={[100, 100]}
/>

            );
            pieChart.setEntryLabelColor(Color.WHITE);
            this.setState({
                chart: pieChart
              });
    }, err => {
        // Print the error if there is one.
        console.log(err);
      });

	};
  render() {
    return (
    <div id = "nutrition">
            <h1>HELLO</h1>
        <div className="dropdown-container">
            <select value={this.state.selectedCountry} onChange={this.handleCountryChange} className="dropdown" id="countryDropdown">
                {this.state.countries}
            </select>
            <button className="submit-btn" id="submitBtn" onClick={this.submitCountryChange}>Submit</button>
        </div>
        <div className="chart" id="chart">
        {this.state.chart}
		</div>
    </div>
    );
  }
}
