import React, { Component } from 'react';
import { MapContainer, CircleMarker, TileLayer,Tooltip } from "react-leaflet";
import "leaflet/dist/leaflet.css";

class Nutrition extends Component {
    constructor(props) {
		super(props);

		this.state = {
			countries: [],
            selectedCountry: ""
		};

		this.submitCountryChange = this.submitCountryChange.bind(this);
	    this.handleCountryChange = this.handleCountryChange.bind(this);
		// this.handleGenreChange = this.handleGenreChange.bind(this);
	};

	/* ---- Q3a (Best Movies) ---- */
	componentDidMount() {

        //fetch the weeks options
        fetch("http://localhost:8081/nutrition",
		{
		  method: 'GET' // The type of HTTP request.
		}).then(res => {
		  // Convert the response data to a JSON.
		  return res.json();
		}, err => {
		  // Print the error if there is one.
		  console.log(err);
	  }).then(countryList => {
		  if (!countryList) return;

		  const countryDivs = countryList.map((countryObj, i) =>
		  <option
		      key={i}
		      className="countryOption"
			  value={countryObj.Date.substring(0,10)}>{countryObj.Date.substring(0,10)}
		  </option>
		  );
        //   console.log(decadesDivs);
		//    console.log(decadesList);
		  this.setState({
			countries: countryDivs,
			selectedCountry: countryDivs[0].props.value,
		  });
		}, err => {
		  // Print the error if there is one.
		  console.log(err);
		});

	};
    handleWeekChange(e) {
		console.log(e.target.value);
        let tmp = e.target.value;
        let date = tmp.substring(0,10);
        console.log(date);
		this.setState({
			selectedWeek: date
		});

	};

    submitWeekChange(e) {
        fetch("http://localhost:8081/byWeek/"+this.state.selectedWeek,
        {
            method: 'GET' // The type of HTTP request.
        }).then(res =>{
            return res.json();
        }, err => {
            // Print the error if there is one.
            console.log(err);
        }).then(casesList => {
            if (!casesList) return;
            const countriesDivs = casesList.map((casesObj, i) =>
        <CircleMarker
            key={i}
            center={[casesObj.latitude, casesObj.longitude]}
            // radius={20 * Math.log(casesObj.Confirmed / 100000)}
            radius={casesObj.Confirmed / 400000}
            fillOpacity={0.5}
            color="red"
            stroke={false}
            name={casesObj.Country}
        >
            <Tooltip direction="right" offset={[-8, -2]} opacity={1}>
                <span>{casesObj.Country }</span>
                <br />
                <span>{ "Confirmed \n" + ": " + casesObj.Confirmed}</span>
                <br />
                <span>{ "Deaths \n" + ": " + casesObj.Deaths}</span>
            </Tooltip>
        </CircleMarker>
        );

        // Set the state of the keywords list to the value returned by the HTTP response from the server.
        this.setState({
            countries:countriesDivs
        });
        console.log(this.state.countries[0]);
    }, err => {
        // Print the error if there is one.
        console.log(err);
      });

	};
  render() {
    return (
        <div>
        <div className="dropdown-container">
        <select value={this.state.selectedCountry} onChange={this.handleCountryChange} className="dropdown" id="countryDropdown">
            {this.state.week}
        </select>
        <button className="submit-btn" id="submitBtn" onClick={this.submitCountryChange}>Submit</button>
        </div>

      </div>
    );
  }
}

export default Nutrition;
